// LibraryTabsFilters.js
import React, { useState, useEffect } from 'react';
import MetadataFilterValues from './MetadataFilterValues';  // Import MetadataFilterValues

function LibraryTabsFilters({ onFilterChange }) {
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [metadataFilters, setMetadataFilters] = useState({});
  const [metadataValues, setMetadataValues] = useState({ metadataHeaders: [], metadataValues: {} });

  useEffect(() => {
    // Use MetadataFilterValues to fetch metadata headers and values
    const handleDataLoaded = (data) => {
      setMetadataValues(data);
    };

    MetadataFilterValues({ onDataLoaded: handleDataLoaded });
  }, []);

  const handleStartDateChange = (e) => {
    setStartDate(e.target.value);
  };

  const handleEndDateChange = (e) => {
    setEndDate(e.target.value);
  };

  const handleMetadataFilterChange = (header, value) => {
    setMetadataFilters((prevFilters) => ({
      ...prevFilters,
      [header]: value,
    }));
  };

  const handleApplyFilters = () => {
    onFilterChange({
      startDate,
      endDate,
      metadataFilters,
    });
  };

  return (
    <div style={{ marginTop: '20px' }}>
      <h3>Filters</h3>
      <div>
        <label>Start Date:</label>
        <input type="date" value={startDate} onChange={handleStartDateChange} />
      </div>
      <div>
        <label>End Date:</label>
        <input type="date" value={endDate} onChange={handleEndDateChange} />
      </div>
      <div>
        <h4>Metadata Filters:</h4>
        {metadataValues.metadataHeaders.map((header) => (
          <div key={header}>
            <label>{header}:</label>
            <select
              value={metadataFilters[header] || ''}
              onChange={(e) => handleMetadataFilterChange(header, e.target.value)}
            >
              <option value="">Select {header}</option>
              {metadataValues.metadataValues[header]?.map((value) => (
                <option key={value} value={value}>
                  {value}
                </option>
              ))}
            </select>
          </div>
        ))}
      </div>
      <button onClick={handleApplyFilters}>Apply Filters</button>
    </div>
  );
}

export default LibraryTabsFilters;
